function initShaders(d,i,g){var f;var e;var b=document.getElementById(i);if(!b){alert("Unable to load vertex shader "+i);return -1}else{f=d.createShader(d.VERTEX_SHADER);d.shaderSource(f,b.text);d.compileShader(f);if(!d.getShaderParameter(f,d.COMPILE_STATUS)){var a="Vertex shader failed to compile.  The error log is:<pre>"+d.getShaderInfoLog(f)+"</pre>";alert(a);return -1}}var h=document.getElementById(g);if(!h){alert("Unable to load Fragment shader "+g);return -1}else{e=d.createShader(d.FRAGMENT_SHADER);d.shaderSource(e,h.text);d.compileShader(e);if(!d.getShaderParameter(e,d.COMPILE_STATUS)){var a="Fragment shader failed to compile.  The error log is:<pre>"+d.getShaderInfoLog(e)+"</pre>";alert(a);return -1}}var c=d.createProgram();d.attachShader(c,f);d.attachShader(c,e);d.linkProgram(c);if(!d.getProgramParameter(c,d.LINK_STATUS)){var a="Shader program failed to link.  The error log is:<pre>"+d.getProgramInfoLog(c)+"</pre>";alert(a);return -1}return c};